package com.emeritus;/*
 * This Java source file was generated by the Gradle 'init' task.
 */
import com.emeritus.Launcher;
import org.junit.Test;

import java.io.ByteArrayOutputStream;
import java.io.PrintStream;

import static org.junit.Assert.*;

public class LauncherIT {
    @Test
    public void launcherShouldAcceptArgumentParseFileAndReturnCorrectSum() throws Exception {

        // Setup
        String expected = "World population is: 1,347,982,728\n";

        // https://stackoverflow.com/questions/8708342/redirect-console-output-to-string-in-java
        ByteArrayOutputStream baos = new ByteArrayOutputStream();
        PrintStream ps = new PrintStream(baos);
        PrintStream old = System.out;
        System.setOut(ps);

        // Exercise
        Launcher.main(new String[] {"./data/worldcitiespop.csv"});

        // Assert
        System.out.flush();
        String actual = baos.toString();
        assertEquals("Population counter should return correct total", expected, actual);

        // Teardown
        System.setOut(old);
    }
}
